import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addTodo, toggleTodo } from "./actions";
im

function Todos() {
  const [taskText, setTaskText] = useState("");
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.todos);

  function handleAddTask() {
    if (taskText.trim() === "") {
      return;
    }
    dispatch(addTodo(taskText));
    setTaskText("");
  }

  function handleToggleTask(task) {
    dispatch(toggleTodo(task.id));
  }

  const incomplete = todos.filter((task) => !task.complete);
  const complete = todos.filter((task) => task.complete);

  return (
    <div>
      <h2>Incomplete Tasks</h2>
      <ul>
        {incomplete.map((task) => (
          <li key={task.id}>
            {task.text}{" "}
            <button onClick={() => handleToggleTask(task)}>Complete</button>
          </li>
        ))}
      </ul>
      <input
        type="text"
        value={taskText}
        onChange={(e) => setTaskText(e.target.value)}
      />
      <button onClick={handleAddTask}>Add Task</button>

      <h2>Complete Tasks</h2>
      <ul>
        {complete.map((task) => (
          <li key={task.id}>
            {task.text}{" "}
            <button onClick={() => handleToggleTask(task)}>Incomplete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Todos;
