import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addTodo, toggleTodo } from "./actions";
import store from "./store";

function Todos() {
  const [taskText, setTaskText] = useState("");
  const dispatch = useDispatch();
  const state = store.getState();
  //console.log(state);

  function handleAddTask() {
    if (taskText.trim() === "") {
      return;
    }
    dispatch(addTodo(taskText));
    setTaskText("");
  }

  function handleToggleTask(task) {
    dispatch(toggleTodo(task.id));
  }

  const incomplete = useSelector((state) => state.incomplete);
  const complete = useSelector((state) => state.complete);

  return (
    <div>
      <input
        type="text"
        value={taskText}
        onChange={(e) => setTaskText(e.target.value)}
      />
      <button onClick={handleAddTask}>Add Task</button>

      <h2>Incomplete Tasks</h2>
      <ul>
        {incomplete.map((task) => (
          <li key={task.id}>
            {task.text}{" "}
            <button onClick={() => handleToggleTask(task)}>Complete</button>
          </li>
        ))}
      </ul>

      <h2>Complete Tasks</h2>
      <ul>
        {complete.map((task) => (
          <li key={task.id}>
            {task.text}{" "}
            <button onClick={() => handleToggleTask(task)}>Incomplete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Todos;
